import React, { useState, useEffect } from "react";
import "./MainEventsPage.css"; // Import CSS file for styling

function MainEventsPage() {
  return (
    <div className="main-events-page">
      <header>{/* Your header content here */}</header>

      <main>
        <section className="upcoming-events-section">
          <h2>Upcoming Events</h2>
          <div className="event-cards-container">
            {upcomingEvents.slice(0, maxEventsToDisplay).map((event) => (
              <div className="event-card" key={event.id}>
                {" "}
                {/* Add event ID for uniqueness */}
                <img src={event.imageUrl} alt={event.title} />
                <div className="event-details">
                  <h4>{event.title}</h4>
                  <p>Date: {event.date}</p>
                  <p>Time: {event.time}</p>
                </div>
              </div>
            ))}
          </div>
          {upcomingEvents.length > maxEventsToDisplay && (
            <a href="#view-more" className="view-more-button">
              View More Upcoming Events
            </a>
          )}
        </section>

        <section className="past-events-section">
          <h2>Past Events</h2>
          <div className="event-cards-container">
            {pastEvents.slice(0, maxEventsToDisplay).map((event) => (
              <div className="event-card" key={event.id}>
                {" "}
                {/* Add event ID for uniqueness */}
                <img src={event.imageUrl} alt={event.title} />
                <div className="event-details">
                  <h4>{event.title}</h4>
                  <p>Date: {event.date}</p>
                  <p>Time: {event.time}</p>
                </div>
              </div>
            ))}
          </div>
          {pastEvents.length > maxEventsToDisplay && (
            <a href="#view-more" className="view-more-button">
              View More Past Events
            </a>
          )}
        </section>
      </main>

      <footer>{/* Your footer content here */}</footer>
    </div>
  );
}

export default MainEventsPage;
